INSERT INTO knowledge_entries (query, category, hit_frequency, solutions, prerequisites, success_indicators, common_pitfalls, success_rate, claude_version, last_verified, source_url, contributor_email) VALUES
('No component factory found for component. Did you add it to @NgModule.entryComponents?', 'angular', 'HIGH', '[{"solution": "Ensure the component is declared in the NgModule declarations array. For dynamically created components in Angular 7 and earlier, add the component to the entryComponents array in @NgModule decorator", "percentage": 95}, {"solution": "Check that directive/component references in HTML files still exist - remove references to deleted components", "percentage": 85}]'::jsonb, 'Angular 7 or earlier, components used dynamically or in production build', 'ng build --prod completes without errors, component renders correctly in production build', 'Forgetting to add components to entryComponents in NgModule, not removing deleted component references from HTML', 0.92, 'haiku', NOW(), 'https://stackoverflow.com/questions/54778667/why-angular-application-throws-error-after-production-build', 'admin:1764191915'),
('ERROR in chunk from Terser --> Unexpected token: punc', 'angular', 'HIGH', '[{"solution": "Check for trailing commas in JavaScript/TypeScript files - this breaks Terser minification in production builds. Remove any trailing commas in the affected code", "percentage": 96}, {"solution": "Disable build optimizer temporarily with --build-optimizer=false flag, then identify and fix the syntax error causing Terser to fail", "percentage": 80}]'::jsonb, 'Angular 7+, using --prod --build-optimizer flags', 'ng build --prod completes without Terser errors, bundle chunks are properly minified', 'Missing or overlooking trailing commas in code that works in dev but breaks during minification', 0.93, 'haiku', NOW(), 'https://stackoverflow.com/questions/54417103/angular-build-for-production-fails', 'admin:1764191915'),
('You have to be inside an angular-cli project in order to use the build command', 'angular', 'HIGH', '[{"solution": "Navigate to the project directory using cd DIRECTORY_NAME after creating the new Angular app with ng new", "percentage": 98}, {"solution": "Verify that angular.json and package.json files exist in the current directory", "percentage": 90}]'::jsonb, 'Angular CLI installed globally, new Angular project created', 'ng build command executes without project detection errors', 'Running ng build from wrong directory level, not entering the project folder after ng new command', 0.95, 'haiku', NOW(), 'https://stackoverflow.com/questions/38333558/build-error-while-using-ng-build', 'admin:1764191915'),
('Error during template compile - Function calls are not supported in decorators but makeDecorator was called in Injectable', 'angular', 'HIGH', '[{"solution": "Check for duplicate Angular core dependencies in node_modules - remove conflicting peer dependencies like adal-angular5 that bundle their own @angular/core", "percentage": 88}, {"solution": "Add peerDependencies to package.json specifying compatible Angular versions: \"peerDependencies\": {\"@angular/common\": \">=X.X.X\"}", "percentage": 85}, {"solution": "Update or remove the conflicting library to one compatible with your Angular version", "percentage": 80}]'::jsonb, 'Angular 6+, third-party libraries with @angular/core dependencies', 'ng build --prod completes, ngDevMode variable is not redeclared', 'Multiple versions of @angular/core in node_modules from transitive dependencies, not checking library compatibility', 0.87, 'haiku', NOW(), 'https://stackoverflow.com/questions/51117887/angular-6-ng-serve-is-running-completely-but-ng-build-is-throwing-an-error', 'admin:1764191915'),
('ERROR in template - Expected 0 arguments, but got 1', 'angular', 'HIGH', '[{"solution": "Check template event handlers - remove unexpected parameters being passed to methods. For example, change closeSearchTerm($event) to closeSearchTerm() if the method expects no arguments", "percentage": 97}, {"solution": "Verify method signatures in component match the template calls - template compiler does strict type checking in production builds", "percentage": 93}]'::jsonb, 'Angular production build with strict template type checking', 'ng build --prod completes without template argument errors, method calls in templates match component signatures', 'Accidentally passing $event or other parameters to methods that don''t accept them, not catching mistakes in dev mode that fail in prod', 0.94, 'haiku', NOW(), 'https://stackoverflow.com/questions/49885119/angular-build-error', 'admin:1764191915');
