INSERT INTO knowledge_entries (query, category, hit_frequency, solutions, prerequisites, success_indicators, common_pitfalls, success_rate, claude_version, last_verified, source_url, contributor_email) VALUES
('[HPM] Error occurred while trying to proxy request /api/file.php from localhost:4200 to http://localhost:1234 (ECONNREFUSED)', 'angular', 'HIGH', '[{"solution": "Use IP address (127.0.0.1) instead of localhost in proxy config target. Change proxy-config target from http://localhost:1234 to http://127.0.0.1:1234", "percentage": 95}, {"solution": "Run ng serve with explicit proxy-config flag instead of npm start to ensure config is loaded", "percentage": 85}, {"solution": "For PHP backend, use ''php -S 127.0.0.1:8000'' instead of ''php -S localhost:8000''", "percentage": 80}]'::jsonb, 'Angular project with proxy-config.json, backend service running on localhost', 'Requests to /api/* complete successfully without 504 Gateway Timeout errors in browser Developer Tools', 'Using ''localhost'' instead of 127.0.0.1 in proxy target, forgetting to run backend service on correct port, not using explicit ng serve command', 0.90, 'haiku', NOW(), 'https://stackoverflow.com/questions/53732761/econnrefused-for-proxy-request-from-localhost-to-another-localhost', 'admin:1764191677');
INSERT INTO knowledge_entries (query, category, hit_frequency, solutions, prerequisites, success_indicators, common_pitfalls, success_rate, claude_version, last_verified, source_url, contributor_email) VALUES
('Unhandled Promise rejection: connect ECONNREFUSED 127.0.0.1:6379', 'angular', 'HIGH', '[{"solution": "Install Redis server and run redis-server.exe (Windows) or redis-server (macOS/Linux) before running npm run dev:ssr", "percentage": 95}, {"solution": "Verify Redis is listening on port 6379 with ''redis-cli ping'' command", "percentage": 90}, {"solution": "For Docker setups, ensure redis container is running and accessible on correct network", "percentage": 85}]'::jsonb, 'Angular SSR project with Redis dependency, Node.js and Redis installed', 'npm run dev:ssr starts without errors, page loads without Promise rejection in console, redis-cli ping returns PONG', 'Forgetting to start Redis server before SSR, Redis service not installed, incorrect Redis port configuration', 0.92, 'haiku', NOW(), 'https://stackoverflow.com/questions/72761275/angular-error-when-run-this-command-npm-run-devssr-to-watch-the-website-how-s', 'admin:1764191677');
INSERT INTO knowledge_entries (query, category, hit_frequency, solutions, prerequisites, success_indicators, common_pitfalls, success_rate, claude_version, last_verified, source_url, contributor_email) VALUES
('[vite] http proxy error: /weatherforecast Error: connect ECONNREFUSED 127.0.0.1:40443', 'angular', 'HIGH', '[{"solution": "Start ASP.NET Core backend application before starting Angular Vite dev server. Set backend as startup project in IDE", "percentage": 95}, {"solution": "Verify backend is running and listening on port 40443 with correct SSL certificate", "percentage": 90}, {"solution": "Ensure proxy config target matches actual backend listening port (e.g., 40443 not 40442)", "percentage": 85}]'::jsonb, 'Angular with Vite proxy setup, ASP.NET Core 8 backend, proxy.conf file configured', 'Vite proxy successfully forwards requests to /weatherforecast, no ECONNREFUSED errors in terminal', 'Starting Angular before backend, incorrect port in proxy config, SSL certificate not properly configured', 0.88, 'haiku', NOW(), 'https://stackoverflow.com/questions/79490763/asp-net-core-8-and-angular-vite-proxy-error-connect-econnrefused-127-0-0-14044', 'admin:1764191677');
INSERT INTO knowledge_entries (query, category, hit_frequency, solutions, prerequisites, success_indicators, common_pitfalls, success_rate, claude_version, last_verified, source_url, contributor_email) VALUES
('Cannot connect to the target: connect ECONNREFUSED 127.0.0.1:9222', 'angular', 'HIGH', '[{"solution": "Launch Chrome with remote debugging enabled using --remote-debugging-port=9222 flag before attaching debugger", "percentage": 95}, {"solution": "Windows: Right-click Chrome shortcut, select Properties, add --remote-debugging-port=9222 to target field", "percentage": 90}, {"solution": "Close all existing Chrome instances before launching debugger-enabled Chrome instance", "percentage": 85}]'::jsonb, 'VS Code Chrome Debugger extension installed, Chrome browser installed, Angular project', 'VS Code debugger successfully attaches to Chrome, breakpoints work, variables inspectable', 'Forgetting to close Chrome instances, Chrome not launched with debugging flag, debugger port already in use', 0.91, 'haiku', NOW(), 'https://stackoverflow.com/questions/36783068/cannot-connect-to-the-target-connect-econnrefused-127-0-0-19222', 'admin:1764191677');
INSERT INTO knowledge_entries (query, category, hit_frequency, solutions, prerequisites, success_indicators, common_pitfalls, success_rate, claude_version, last_verified, source_url, contributor_email) VALUES
('[HPM] Error occurred while trying to proxy request /api/test from mysite.local:4200 to http://mysite.local/api/ (ECONNREFUSED)', 'angular', 'HIGH', '[{"solution": "Use Docker service name instead of localhost/domain name in proxy target. Change target to service name (e.g., ''web'' or ''api'') defined in docker-compose.yml", "percentage": 95}, {"solution": "Verify all services are in same Docker network and service names match docker-compose service definitions", "percentage": 90}, {"solution": "Use port defined in docker-compose instead of external host port", "percentage": 85}]'::jsonb, 'Angular app in Docker container, proxy.conf.json configured, docker-compose.yml with multiple services', 'Angular proxy forwards requests to correct backend service, no ECONNREFUSED errors in Docker logs', 'Using localhost/domain name instead of Docker service name, incorrect port number, services not on same network', 0.90, 'haiku', NOW(), 'https://stackoverflow.com/questions/59452725/angular-cli-proxy-wont-connect-through-docker-econnrefused', 'admin:1764191677');
INSERT INTO knowledge_entries (query, category, hit_frequency, solutions, prerequisites, success_indicators, common_pitfalls, success_rate, claude_version, last_verified, source_url, contributor_email) VALUES
('Error: connect ECONNREFUSED 104.16.21.35:80 (ng update)', 'angular', 'HIGH', '[{"solution": "Configure npm to work through corporate proxy using ''npm config set proxy http://domain\\\\username:password@servername:port/''", "percentage": 90}, {"solution": "Check npm proxy settings with ''npm config get proxy'' and configure if behind corporate firewall", "percentage": 88}, {"solution": "Remove saved proxy password after use with ''npm config delete proxy''", "percentage": 85}]'::jsonb, 'Angular CLI installed, corporate proxy environment, npm configured', 'ng update succeeds without connection refused errors, packages download successfully', 'Proxy configuration incorrect or missing, password containing special characters not escaped, proxy protocol mismatch', 0.87, 'haiku', NOW(), 'https://stackoverflow.com/questions/52598035/ng-update-error-connect-econnrefused-104-16-21-3580-at-object-errnoexception', 'admin:1764191677');
INSERT INTO knowledge_entries (query, category, hit_frequency, solutions, prerequisites, success_indicators, common_pitfalls, success_rate, claude_version, last_verified, source_url, contributor_email) VALUES
('GET https://localhost:8443/sb-template/status-routes net::ERR_CONNECTION_REFUSED', 'angular', 'HIGH', '[{"solution": "Check HttpErrorResponse with status code 0 to identify connection refused errors. Status 0 indicates request was not completed", "percentage": 92}, {"solution": "Verify backend service is running on correct port (8443) and SSL certificate is valid", "percentage": 88}, {"solution": "Add CORS headers (Access-Control-Allow-Origin, Access-Control-Allow-Methods) to backend if different origin", "percentage": 82}]'::jsonb, 'Angular app with HttpClient, backend service on HTTPS, HttpErrorResponse handling', 'HTTP requests complete successfully, no ERR_CONNECTION_REFUSED in browser console, status code is not 0', 'Not checking status code 0 in error handler, backend not running or listening on wrong port, CORS headers missing', 0.89, 'haiku', NOW(), 'https://stackoverflow.com/questions/53875346/angular6-httpclient-error-handling-of-get-neterr-connection-refused', 'admin:1764191677');
INSERT INTO knowledge_entries (query, category, hit_frequency, solutions, prerequisites, success_indicators, common_pitfalls, success_rate, claude_version, last_verified, source_url, contributor_email) VALUES
('OPTIONS http://localhost:3000/api/findCoinData net::ERR_CONNECTION_REFUSED', 'angular', 'HIGH', '[{"solution": "Ensure backend API is running on correct port (3000) and accessible. OPTIONS is CORS preflight request", "percentage": 93}, {"solution": "Add proper CORS headers to backend: Access-Control-Allow-Origin, Access-Control-Allow-Methods, Access-Control-Allow-Headers", "percentage": 90}, {"solution": "Verify ng serve is running with correct proxy-config if using proxy setup", "percentage": 85}]'::jsonb, 'Angular HttpClient making requests, backend API running, CORS configuration needed', 'HttpClient requests succeed without OPTIONS preflight errors, API responses received correctly', 'Backend not running on expected port, missing CORS headers, incorrect proxy configuration, ng serve not using proxy-config', 0.91, 'haiku', NOW(), 'https://stackoverflow.com/questions/47638052/httpclient-get-gives-err-connection-refused-on-using-ng-serve', 'admin:1764191677');
INSERT INTO knowledge_entries (query, category, hit_frequency, solutions, prerequisites, success_indicators, common_pitfalls, success_rate, claude_version, last_verified, source_url, contributor_email) VALUES
('http://localhost:3000/api/heroes net::ERR_CONNECTION_REFUSED', 'angular', 'HIGH', '[{"solution": "Handle status code 0 in HttpErrorResponse to catch connection refused errors. Use ''if (error.status === 0)'' in error handler", "percentage": 92}, {"solution": "Use RxJS throwError() to return user-friendly error message ''Unable to Connect to the Server''", "percentage": 90}, {"solution": "Implement HttpInterceptor to handle all connection errors globally", "percentage": 88}]'::jsonb, 'Angular with HttpClient, HttpErrorResponse error handling, RxJS 6+', 'Connection errors display friendly message to user, error handler catches status 0 errors, app handles server unavailability gracefully', 'Not checking status code 0, assuming connection errors will have standard HTTP status codes, using deprecated error handling patterns', 0.90, 'haiku', NOW(), 'https://stackoverflow.com/questions/41354553/how-to-handle-neterr-connection-refused-in-angular2', 'admin:1764191677');
INSERT INTO knowledge_entries (query, category, hit_frequency, solutions, prerequisites, success_indicators, common_pitfalls, success_rate, claude_version, last_verified, source_url, contributor_email) VALUES
('[HPM] POST /api/v1/login ~> https://demo.zhaidongxi.com ETIMEDOUT when behind corporate proxy', 'angular', 'HIGH', '[{"solution": "Install https-proxy-agent: npm install --save-dev https-proxy-agent and configure proxy config to use it", "percentage": 93}, {"solution": "Set environment variable http_proxy or HTTP_PROXY pointing to corporate proxy, then configure agent in proxy setup", "percentage": 91}, {"solution": "Create JavaScript proxy config file that uses HttpsProxyAgent to route external API calls through corporate proxy", "percentage": 88}]'::jsonb, 'Angular behind corporate proxy, external API calls in proxy config, https-proxy-agent installed', 'External API calls succeed through corporate proxy, no ETIMEDOUT errors, proxy logs show requests routing correctly', 'Missing https-proxy-agent installation, environment variable not set correctly, proxy agent not configured in proxy setup', 0.89, 'haiku', NOW(), 'https://stackoverflow.com/questions/58855374/angular-proxy-timeout-when-trying-to-reach-extrenal-api', 'admin:1764191677');
