INSERT INTO knowledge_entries (query, category, hit_frequency, solutions, prerequisites, success_indicators, common_pitfalls, success_rate, claude_version, last_verified, source_url, contributor_email) VALUES
('GORM MySQL build failed: undefined: mysql.Open', 'database', 'HIGH', '[{"solution": "Replace import ''github.com/go-sql-driver/mysql'' with ''gorm.io/driver/mysql'' in your imports. This package includes the proper mysql.Open() function required by GORM v2.", "percentage": 95}, {"solution": "Ensure your GORM dependency is updated to v2+ using ''go get -u gorm.io/gorm''", "percentage": 85}]'::jsonb, 'Go 1.16+, GORM v2, MySQL driver package', 'DB connection initializes without panic, AutoMigrate succeeds, Create operations work without import errors', 'Using old gorm/dialects/mysql path instead of new driver structure, mixing v1 and v2 GORM imports', 0.92, 'haiku', NOW(), 'https://stackoverflow.com/questions/68963996/using-gorm-getting-undefined-mysql-open', 'admin:1764191500'),

('Knex PostgreSQL build failed: connection refused or pool timeout', 'database', 'HIGH', '[{"solution": "Update knex and pg packages to compatible versions: knex@0.95.11 and pg@8.7.1 minimum. Run ''npm install knex@latest pg@latest''", "percentage": 94}, {"solution": "Verify PostgreSQL server is running with ''brew services start postgresql'' on macOS or ''systemctl start postgresql'' on Linux", "percentage": 88}]'::jsonb, 'Node.js 14+, Knex.js, PostgreSQL 12+, pg driver', 'knex.schema operations complete without timeout errors, table creation succeeds, pool connections established', 'Incorrect host/port in knex config (use 127.0.0.1:5432), connection string format errors, missing pg package', 0.90, 'haiku', NOW(), 'https://stackoverflow.com/questions/70690822/knex-cant-build-a-postgresql-connection-in-the-right-db-configuration', 'admin:1764191500'),

('Prisma SQLite build failed: Cannot open database because directory does not exist', 'database', 'HIGH', '[{"solution": "SQLite is incompatible with serverless platforms like Vercel. Migrate to cloud database: use Turso (SQLite alternative), Vercel Postgres, or Neon PostgreSQL instead", "percentage": 98}, {"solution": "For local development, use file:./dev.db path. For production, switch to PostgreSQL connection string", "percentage": 85}]'::jsonb, 'Prisma 4.0+, Node.js 14+, deployed on serverless platform', 'Database operations work without file system errors, deployment completes on serverless platform, production uses PostgreSQL', 'Attempting to use local SQLite file path on serverless (Vercel, AWS Lambda), read-only filesystem assumptions', 0.96, 'haiku', NOW(), 'https://stackoverflow.com/questions/79001183/typeerror-cannot-open-database-because-the-directory-does-not-exist-in-next-js', 'admin:1764191500'),

('Prisma Turso LibSQL build failed: Driver adapter schema provider mismatch', 'database', 'HIGH', '[{"solution": "Change datasource provider from ''postgresql'' to ''sqlite'' in schema.prisma. Add previewFeatures = [''driverAdapters''] to generator client block. Use @prisma/adapter-libsql with turso driver", "percentage": 93}, {"solution": "Verify TURSO_DATABASE_URL format is ''libsql://dbname.turso.io'' and TURSO_AUTH_TOKEN is set in environment variables", "percentage": 89}]'::jsonb, 'Prisma 5.0+, Turso account, libsql package, @prisma/adapter-libsql', 'Prisma client initializes without adapter errors, generate command succeeds, Turso database queries execute successfully', 'Mixing postgresql provider with libsql adapter, incorrect Turso URL format, missing authToken in client config', 0.91, 'haiku', NOW(), 'https://stackoverflow.com/questions/79313328/prisma-turso-db-error-in-next-js-project-driver-adapter-prisma-adapter-libsql', 'admin:1764191500'),

('GORM PostgreSQL build failed: undefined gorm.ColumnType in migrator', 'database', 'HIGH', '[{"solution": "Replace old import ''github.com/jinzhu/gorm'' with ''gorm.io/gorm'' and use ''gorm.io/driver/postgres'' instead of dialects/postgres. Update to GORM v2 package structure", "percentage": 96}, {"solution": "Upgrade GORM to version 1.20.7+ if using v1, or migrate fully to gorm.io v2+ packages", "percentage": 88}]'::jsonb, 'Go 1.16+, GORM v2, PostgreSQL driver', 'Build completes without undefined reference errors, migrations run successfully, database connections open without panic', 'Mixing old and new GORM import paths, using v1 dialect packages with v2 driver syntax, outdated GORM versions', 0.94, 'haiku', NOW(), 'https://stackoverflow.com/questions/64537917/gorm-failed-when-changing-to-using-pgx-migrator-go20665-undefined-gorm-colu', 'admin:1764191500'),

('SQLAlchemy PostgreSQL build failed: NoSuchModuleError postgres dialect', 'database', 'HIGH', '[{"solution": "Update database URI from ''postgres://'' to ''postgresql://'' prefix. SQLAlchemy 1.4+ removed support for ''postgres'' dialect name", "percentage": 97}, {"solution": "If using Heroku DATABASE_URL, replace it in your config to use ''postgresql+psycopg2://'' instead of ''postgres://''", "percentage": 92}]'::jsonb, 'SQLAlchemy 1.4+, Python 3.7+, psycopg2 driver', 'Database URI connects without NoSuchModuleError, SQLAlchemy ORM sessions initialize, migrations apply successfully', 'Using ''postgres://'' URI prefix instead of ''postgresql://'', outdated SQLAlchemy versions, incorrect dialect name specification', 0.95, 'haiku', NOW(), 'https://stackoverflow.com/questions/62688256/sqlalchemy-exc-nosuchmoduleerror-cant-load-plugin-sqlalchemy-dialectspostgre', 'admin:1764191500'),

('ActiveRecord MySQL build failed: PendingMigrationError with existing database', 'database', 'HIGH', '[{"solution": "Create schema_migrations table manually: CREATE TABLE schema_migrations (version varchar(255) NOT NULL PRIMARY KEY). Then run ''rake db:migrate'' to register pending migrations", "percentage": 91}, {"solution": "Skip migrations for existing DB by adding ''config.active_record.schema_format = :sql'' to database.yml, then use schema dump instead", "percentage": 82}]'::jsonb, 'Rails 4.2+, Ruby 2.2+, MySQL 5.7+, write access to database', 'rake db:migrate completes, schema_migrations table exists with version entries, ActiveRecord finds all migrations', 'Missing schema_migrations table on existing databases, missing write permissions, running migrations on read-only connection', 0.88, 'haiku', NOW(), 'https://stackoverflow.com/questions/32640362/activerecordpendingmigrationerror-when-trying-to-access-data-from-remote-mysql', 'admin:1764191500'),

('Diesel PostgreSQL build failed: Received empty query in migration file', 'database', 'HIGH', '[{"solution": "Verify migration SQL files are in the correct folder: migrations/YYYY-MM-DD-HHMMSS_name/{up.sql,down.sql}. Check that up.sql contains valid SQL and is not empty", "percentage": 94}, {"solution": "If you ran ''diesel migration generate'' multiple times, delete duplicate migration folders and ensure only one migration per timestamp exists", "percentage": 89}]'::jsonb, 'Rust 1.57+, Diesel 1.4+, PostgreSQL 13+, diesel CLI installed', 'diesel migration run completes without empty query error, migrations apply to schema.rs, database reflects migration changes', 'SQL files in wrong directory structure, empty migration files, duplicate migration folders with same timestamp', 0.92, 'haiku', NOW(), 'https://stackoverflow.com/questions/70707096/diesel-migration-run-failed-for-postgresql-at-the-first-beginning', 'admin:1764191500'),

('Sequelize Redis build failed: ENOTFOUND hostname in docker-compose', 'database', 'HIGH', '[{"solution": "When running from Docker host machine, use ''localhost:6379'' instead of container name ''my-redis''. For inter-container communication, use service name with depends_on in docker-compose", "percentage": 93}, {"solution": "Ensure Redis container is running: ''docker ps'' should show redis service. Verify port mapping is correct: 0.0.0.0:6379->6379/tcp", "percentage": 87}]'::jsonb, 'Docker, docker-compose, Node.js, redis package', 'Redis connection established from Sequelize seeders, docker-compose exec works, ENOTFOUND errors disappear', 'Using container name as hostname from host machine, Redis service not running, incorrect docker-compose port mapping', 0.89, 'haiku', NOW(), 'https://stackoverflow.com/questions/67884413/redis-connection-to-my-redis6379-failed-getaddrinfo-enotfound-when-running-se', 'admin:1764191500'),

('Knex Redis cache build failed: ECONNREFUSED in Docker Apollo Server', 'database', 'HIGH', '[{"solution": "Inside Docker container, use ''redis://redis:6379'' (service name) instead of localhost. For local testing, use ''redis://host.docker.internal:6379'' on Mac/Windows or ''redis://172.17.0.1:6379'' on Linux", "percentage": 95}, {"solution": "Verify Redis is running outside container before building Docker image. Use ''docker run --network host'' only if Redis is on host machine, otherwise use service name in compose file", "percentage": 88}]'::jsonb, 'Docker, Redis installed, Apollo Server 4.0+, Keyv adapter', 'Apollo Server connects to Redis without ECONNREFUSED error, Keyv cache operations succeed, Docker runs with correct networking', 'Using localhost:6379 inside Docker container without network host flag, incorrect Redis URL for containerized apps, Redis not running', 0.91, 'haiku', NOW(), 'https://stackoverflow.com/questions/77961901/apollo-server-4-0-running-inside-of-docker-cant-connect-with-redis-cache-on-loc', 'admin:1764191500');
