INSERT INTO knowledge_entries (query, category, hit_frequency, solutions, prerequisites, success_indicators, common_pitfalls, success_rate, claude_version, last_verified, source_url, contributor_email) VALUES
('Proxy error: Could not proxy request /users from localhost:3000 to http://localhost/3001. ECONNREFUSED', 'react', 'HIGH', '[{"solution": "Check backend port configuration - ensure Express server is running on correct port (e.g., 3001, not 3000). Verify package.json proxy setting matches actual backend port: \"proxy\": \"http://localhost:3001\"", "percentage": 95}, {"solution": "Restart both frontend and backend servers after fixing port configuration", "percentage": 85}]'::jsonb, 'Node/Express backend running, React app with package.json proxy configured', 'Frontend successfully fetches data from backend without ECONNREFUSED errors, network tab shows successful requests', 'Using wrong port in proxy setting, backend not started before frontend, assuming /3001 when actually on different port', 0.92, 'haiku', NOW(), 'https://stackoverflow.com/questions/50033153/reactexpress-getting-error-econnrefused-when-trying-to-grab-data-with-fetch', 'admin:1764192073'),
('ECONNREFUSED error when frontend tries to render before nodemon can restart node server, half the time connection fails', 'react', 'HIGH', '[{"solution": "Replace package.json proxy with CORS headers - use axios config to explicitly set baseURL instead: import axios from ''axios''; export const app = axios.create({ baseURL: ''http://localhost:5000/api/'', withCredentials: true })", "percentage": 95}, {"solution": "Use exponential backoff for retry logic when connection fails", "percentage": 80}, {"solution": "Run separate npm scripts with concurrently: dev script runs both backend and frontend - avoid nodemon''s restart causing timing issues", "percentage": 85}]'::jsonb, 'MERN stack with nodemon, create-react-app frontend, Node backend on separate port', 'Consistent successful connections to backend API, no intermittent ECONNREFUSED errors on page refresh', 'Using package.json proxy which is unreliable with nodemon, not waiting for server restart before frontend connects, mixing proxy with fetch calls', 0.91, 'haiku', NOW(), 'https://stackoverflow.com/questions/52483349/econnrefused-error-for-create-react-app-and-node-server', 'admin:1764192073'),
('Error connection refused in React console when third-party service not running, repeated every 5 seconds fills console with errors', 'react', 'HIGH', '[{"solution": "Wrap fetch calls in try-catch and suppress console output instead of logging - catch(error) => { /* silent fail */ } instead of console.log(error)", "percentage": 90}, {"solution": "Implement exponential backoff to increase time between retry attempts instead of fixed 5-second interval", "percentage": 85}, {"solution": "In Chrome DevTools click gear icon and select ''hide network'' to filter out network errors from console", "percentage": 75}]'::jsonb, 'Polling third-party API with setInterval, fetch or axios with error handling', 'Errors no longer fill console, polling continues silently with exponential backoff, errors handled without noisy logging', 'Logging every connection error, using fixed interval for retries, expecting all calls to succeed when service may be unavailable', 0.88, 'haiku', NOW(), 'https://stackoverflow.com/questions/62718836/how-to-handle-econnrefused-in-react-with-fetch', 'admin:1764192073'),
('ECONNREFUSED error with axios.get initialized outside React component with MSW mocking - request not caught by mock server', 'react', 'MEDIUM', '[{"solution": "Move axios.get() call inside useEffect hook or React component lifecycle - MSW server may not be fully initialized when code executes at import time", "percentage": 92}, {"solution": "Use dynamic import with require() to defer component import until test execution: const { App } = require(''./App''); inside test function", "percentage": 88}, {"solution": "Ensure MSW server is fully started before triggering any API requests - initialize requests only after component mounts", "percentage": 85}]'::jsonb, 'React testing with MSW (Mock Service Worker), axios for HTTP requests, jest with react-testing-library', 'API requests made inside useEffect are caught by MSW handlers without errors, test passes with mocked responses', 'Initializing API calls at import/module level before MSW server starts, making requests synchronously at import time, not deferring requests until test setup complete', 0.89, 'haiku', NOW(), 'https://stackoverflow.com/questions/71961138/error-connect-econnrefused-127-0-0-180-if-an-axios-fetch-gets-initialized-outs', 'admin:1764192073');
