INSERT INTO knowledge_entries (query, category, hit_frequency, solutions, prerequisites, success_indicators, common_pitfalls, success_rate, claude_version, last_verified, source_url, contributor_email) VALUES
('ERROR watch ENOSPC: no space left on device in React Native', 'react', 'HIGH', '[{"solution": "Install watchman to handle file watching properly. Run: brew install watchman (macOS) or apt-get install watchman (Linux)", "percentage": 95}, {"solution": "Increase inotify max_user_watches limit: echo fs.inotify.max_user_watches=524288 | sudo tee -a /etc/sysctl.conf && sudo sysctl -p", "percentage": 85}]'::jsonb, 'Node.js and React development environment set up', 'React Native dev server starts without ENOSPC error, yarn start or npm start completes successfully', 'Not restarting the development server after installing watchman, confusing disk space shortage with inotify limit', 0.92, 'haiku', NOW(), 'https://stackoverflow.com/questions/33965524/error-while-running-react-native-start/33965727', 'admin:1764191974'),
('ENOSPC: no space left on device when running create-react-app', 'react', 'HIGH', '[{"solution": "Increase file watch limit with: echo fs.inotify.max_user_watches=524288 | sudo tee -a /etc/sysctl.conf && sudo sysctl -p", "percentage": 96}, {"solution": "For Arch Linux, add fs.inotify.max_user_watches=524288 to /etc/sysctl.d/99-sysctl.conf and run: sysctl --system", "percentage": 90}]'::jsonb, 'Linux system with create-react-app installed, file system has available space', 'yarn start or npm start runs without ENOSPC error, file watchers initialize successfully', 'Assuming /tmp is full instead of checking inotify limits, not restarting shell after changing sysctl settings', 0.94, 'haiku', NOW(), 'https://stackoverflow.com/questions/50793920/enospc-error-in-create-react-app', 'admin:1764191974'),
('npm ERR! code EACCES: permission denied, symlink when installing create-react-app globally', 'react', 'HIGH', '[{"solution": "Use sudo to install globally: sudo npm install -g create-react-app, then exit root session with exit", "percentage": 94}, {"solution": "Fix npm permissions by configuring npm to use a different directory: npm config set prefix ~/.npm-global and add ~/.npm-global/bin to PATH", "percentage": 88}]'::jsonb, 'Node.js and npm installed, user lacks write permissions to /usr/local/bin', 'create-react-app command available in terminal, npx create-react-app my-app executes successfully', 'Running sudo npm without exiting root session afterward, not checking file permissions on npm directories', 0.91, 'haiku', NOW(), 'https://stackoverflow.com/questions/60818437/npm-create-react-app-error-eacces-permission-denied-the-operation-was-rejecte', 'admin:1764191974'),
('npm ERR! code EPERM: operation not permitted, rmdir when installing React (Windows/macOS)', 'react', 'HIGH', '[{"solution": "Delete npm cache and app data: delete C:\\Users\\[username]\\AppData\\Roaming\\npm and C:\\Users\\[username]\\AppData\\Roaming\\npm-cache folders, then retry npm install", "percentage": 93}, {"solution": "Run npm cache verify to check cache integrity, then retry the installation", "percentage": 82}]'::jsonb, 'Windows or macOS system with npm, sufficient disk space and administrative privileges', 'npm install completes without EPERM errors, create-react-app installation finishes successfully', 'Not running as administrator on Windows, leaving antivirus software interfering with npm operations, corrupted node_modules from partial install', 0.89, 'haiku', NOW(), 'https://stackoverflow.com/questions/45686727/npm-err-error-eperm-operation-not-permitted', 'admin:1764191974'),
('Uncaught ReferenceError: React is not defined in JSX files', 'react', 'HIGH', '[{"solution": "Add React import at top of file: import React from ''react'' before JSX code. For React 17+, use Babel config with runtime automatic: {\"presets\": [[\"@babel/preset-react\", {\"runtime\": \"automatic\"}]]}", "percentage": 97}, {"solution": "Ensure babel-loader is configured in webpack.config.js with presets [''es2015'', ''react''] or equivalent", "percentage": 91}]'::jsonb, 'React project with JSX files, Babel configured for JSX transformation', 'Component renders without ReferenceError, browser console shows no React is not defined error', 'Forgetting React import when not using automatic JSX runtime, not updating Babel configuration after upgrading React, mixing require and import statements', 0.95, 'haiku', NOW(), 'https://stackoverflow.com/questions/32070303/uncaught-referenceerror-react-is-not-defined', 'admin:1764191974');
