INSERT INTO knowledge_entries (query, category, hit_frequency, solutions, prerequisites, success_indicators, common_pitfalls, success_rate, claude_version, last_verified, source_url, contributor_email) VALUES
('Make sure your app is wrapped in a Router error with useNavigate()', 'solidjs', 'HIGH', '[{"solution": "Call useNavigate() at the top level of the component during render, not in event handlers. Call it synchronously during render context.", "percentage": 92}, {"solution": "Ensure your Router wraps all components using useNavigate(). Use HashRouter for SPAs to avoid 404 on page refresh.", "percentage": 88}]'::jsonb, 'SolidJS Router installed', 'Navigation works without errors', 'Calling useNavigate() in callbacks instead of render context', 0.90, 'haiku', NOW(), 'https://stackoverflow.com/questions/72112554/solidjs-router-usenavigate-throws-error-make-sure-your-app-is-wrapped-in-a', 'admin:1764210736'),
('Property use:directive does not exist on type JSXAttributes in SolidJS', 'solidjs', 'HIGH', '[{"solution": "Declare the directive in JSX namespace: declare module ''solid-js'' { namespace JSX { interface Directives { yourDirective: type; } } }", "percentage": 94}, {"solution": "Reference the directive to prevent tree-shaking: true && directiveName", "percentage": 85}]'::jsonb, 'TypeScript JSX files', 'Directive compiles without type errors', 'Forgetting to declare directives or allowing tree-shaking to remove them', 0.89, 'haiku', NOW(), 'https://stackoverflow.com/questions/76915621/solidjs-directives-with-use-throw-errors-for-typescript-in-jsx', 'admin:1764210736'),
('ReferenceError: React is not defined when using SolidJS packages', 'solidjs', 'HIGH', '[{"solution": "Add vite-plugin-solid to vite.config.ts plugins and configure jsxImportSource: ''solid-js''", "percentage": 96}, {"solution": "Exclude problematic packages from vite optimizeDeps to prevent esbuild pre-bundling conflicts", "percentage": 87}]'::jsonb, 'Vite + SolidJS setup', 'Package imports without React reference errors', 'Misconfigured JSX transpiler or vite dependency pre-bundling', 0.93, 'haiku', NOW(), 'https://stackoverflow.com/questions/76683361/solid-js-keep-complaining-about-missing-react-when-it-shouldnt', 'admin:1764210736'),
('Unhandled error when testing createResource with Vitest - resource errors not caught', 'solidjs', 'HIGH', '[{"solution": "Wrap components using createResource in an ErrorBoundary component to catch promise rejections", "percentage": 91}, {"solution": "Use data.error signal to conditionally render error state instead of relying on thrown errors", "percentage": 89}]'::jsonb, 'SolidJS + Vitest setup', 'No unhandled promise rejections in tests', 'Using Show instead of ErrorBoundary for async errors, ignoring resource.error signal', 0.90, 'haiku', NOW(), 'https://stackoverflow.com/questions/72578278/solidjs-unhandled-error-when-testing-data-error-in-createresource-with-vi', 'admin:1764210736'),
('Error: router primitives can be only used inside a Route in SolidJS', 'solidjs', 'HIGH', '[{"solution": "Move router-dependent components (A, useNavigate) inside Route components or use layout root pattern", "percentage": 93}, {"solution": "Use Router root prop with layout component containing navigation: <Router root={(props) => <Layout>{props.children}</Layout>}>", "percentage": 90}]'::jsonb, 'SolidJS Router structure', 'Navigation links work without context errors', 'Placing A or router hooks outside Route context, incorrect component tree structure', 0.91, 'haiku', NOW(), 'https://stackoverflow.com/questions/78245511/solidjs-uncaught-error-a-and-use-router-primitives-can-be-only-used-inside', 'admin:1764210736'),
('For loop in child component not rendering when signal updates', 'solidjs', 'MEDIUM', '[{"solution": "Never destructure props in components: use props.signal directly instead of const {signal} = props", "percentage": 97}, {"solution": "Props object uses reactive getters - destructuring breaks reactivity and loses subscription to updates", "percentage": 95}]'::jsonb, 'SolidJS component props', 'Child components re-render when parent signals update', 'Destructuring props loses reactivity, passing signal() instead of signal to child', 0.96, 'haiku', NOW(), 'https://stackoverflow.com/questions/70842938/solidjs-for-in-child-component-not-rendering-list', 'admin:1764210736'),
('Signal passed to component does not cause re-render when set in createEffect', 'solidjs', 'HIGH', '[{"solution": "Access props.signal as a function directly in JSX, never destructure: {props.signal()} instead of {signal}", "percentage": 96}, {"solution": "Ensure setters are called with new values - createEffect reads reactive values to subscribe them", "percentage": 93}]'::jsonb, 'SolidJS signal passing', 'Parent signal updates trigger child re-renders', 'Destructuring props at component level, breaking reactive getter chain', 0.94, 'haiku', NOW(), 'https://stackoverflow.com/questions/76996417/using-a-component-and-passing-down-a-signal-doesnt-cause-re-render', 'admin:1764210736'),
('Early return in SolidJS component prevents reactive updates', 'solidjs', 'MEDIUM', '[{"solution": "Move early returns into JSX ternary: {condition() ? null : <Component />} instead of if-return", "percentage": 94}, {"solution": "Use createMemo to wrap returned JSX when early returns are needed: createMemo(() => condition() ? null : <JSX />)", "percentage": 88}]'::jsonb, 'SolidJS component lifecycle', 'Early returns execute when signals update', 'Components execute once at load - reactive contexts needed for re-execution, early returns short-circuit reactivity', 0.91, 'haiku', NOW(), 'https://stackoverflow.com/questions/78037797/why-cant-i-have-early-returns-in-solidjs', 'admin:1764210736'),
('DOM elements not reactive to signal updates when mutating arrays', 'solidjs', 'MEDIUM', '[{"solution": "Create new array instead of mutating: setItems([...items()]) triggers reactivity via === check", "percentage": 95}, {"solution": "Use signal option { equals: false } to disable referential equality checks: createSignal([], { equals: false })", "percentage": 87}]'::jsonb, 'SolidJS signal mutations', 'DOM updates when array signals change', 'Mutating array in place breaks reactivity, signal setter only updates on !== values', 0.92, 'haiku', NOW(), 'https://stackoverflow.com/questions/73655691/solidjs-how-to-make-dom-elements-reactive-to-signal-updates', 'admin:1764210736'),
('Catching exceptions while following SOLID principles in async code', 'solidjs', 'MEDIUM', '[{"solution": "Use resource API with createResource instead of raw promises for async error handling", "percentage": 89}, {"solution": "Centralize error handling with ErrorBoundary for promise rejections across all routes", "percentage": 85}]'::jsonb, 'SolidJS error boundaries', 'Errors caught centrally without manual try-catch per component', 'Not using ErrorBoundary, throwing errors from event handlers, async errors not converted to sync', 0.87, 'haiku', NOW(), 'https://stackoverflow.com/questions/78232026/canonical-way-to-handle-exceptions-in-async-functions-in-solid-js', 'admin:1764210736');
