INSERT INTO knowledge_entries (query, category, hit_frequency, solutions, prerequisites, success_indicators, common_pitfalls, success_rate, claude_version, last_verified, source_url, contributor_email) VALUES
('error() not caught in SvelteKit +error.svelte', 'svelte', 'HIGH', '[{"solution": "error() is for load functions only. Use <svelte:window on:error={handleError}/> in +layout.svelte for global client-side error handling", "percentage": 92}, {"solution": "Move error-throwing logic to a load function in +page.server.ts", "percentage": 85}]'::jsonb, 'SvelteKit ^2.0', 'Error page renders on error', 'Using error() in client-side component code instead of load functions', 0.90, 'haiku', NOW(), 'https://stackoverflow.com/questions/79474193/sveltekit-error-handling-not-working-as-expected', 'admin:1764210798'),
('Global error handling SvelteKit client-side uncaught exceptions', 'svelte', 'HIGH', '[{"solution": "Place <svelte:window on:error={handleError}/> in +layout.svelte to catch all client-side errors globally", "percentage": 94}, {"solution": "Use try-catch blocks with toast notifications or inline error components for async errors", "percentage": 88}]'::jsonb, 'SvelteKit, Svelte 3+', 'Errors caught without white page of death', 'Trying to route to +error.svelte from client-side code directly', 0.91, 'haiku', NOW(), 'https://stackoverflow.com/questions/76141595/client-side-global-error-handling-in-sveltekit', 'admin:1764210798'),
('Custom 404 page SvelteKit +error.svelte routing', 'svelte', 'HIGH', '[{"solution": "Create src/routes/+error.svelte with access to $page.status and $page.error for error display", "percentage": 96}, {"solution": "Use src/routes/[...path]/+page.js to throw error(404) for catch-all routes", "percentage": 93}]'::jsonb, 'SvelteKit ^2.0', 'Custom error page renders for 404 and other status codes', 'Using outdated __error.svelte pattern from SvelteKit v1', 0.95, 'haiku', NOW(), 'https://stackoverflow.com/questions/69321948/how-to-add-a-custom-404-page-and-a-different-error-page-for-other-errors-in-sv', 'admin:1764210798'),
('Form validation errors SvelteKit reactive state management', 'svelte', 'HIGH', '[{"solution": "Use reactive errors object: let errors = {}; and update on form submission failures", "percentage": 97}, {"solution": "Bind error messages to form fields declaratively with #each loops instead of DOM manipulation", "percentage": 95}]'::jsonb, 'Svelte 3+, fetch API', 'Form displays field-specific error messages below inputs', 'Using getElementById() to manually insert errors instead of reactive state', 0.96, 'haiku', NOW(), 'https://stackoverflow.com/questions/58478635/proper-error-handling-with-svelte-from-api-rails', 'admin:1764210798'),
('UnhandledPromiseRejection error() called in async function SvelteKit', 'svelte', 'MEDIUM', '[{"solution": "error() is for load functions only, not async callbacks. Use try-catch and conditional rendering instead", "percentage": 89}, {"solution": "Do not call error() inside onMount or async callbacks - move logic to +page.server.js", "percentage": 91}]'::jsonb, 'SvelteKit, async/await', 'No UnhandledPromiseRejection errors in console', 'Calling error() from inside async functions or event handlers', 0.88, 'haiku', NOW(), 'https://stackoverflow.com/questions/78064520/how-to-call-error-from-within-an-async-function-in-svelte', 'admin:1764210798'),
('TypeError Cannot read properties of undefined reassigned Svelte compiler', 'svelte', 'MEDIUM', '[{"solution": "Use object destructuring with {} default instead of empty string: export let { href, brand } = {}", "percentage": 91}, {"solution": "Declare props separately instead of destructuring: export let href; export let brand;", "percentage": 93}]'::jsonb, 'Svelte 4+', 'Component builds without reassignment errors', 'Destructuring props with primitive defaults like empty string or number', 0.92, 'haiku', NOW(), 'https://stackoverflow.com/questions/76566184/svelte-compiler-gives-me-an-error-in-my-icon-component-file-while-building-wh', 'admin:1764210798'),
('Catch server errors Sapper +error template rendering', 'svelte', 'MEDIUM', '[{"solution": "Use @crownframework/svelte-error-boundary component to catch errors at component level", "percentage": 87}, {"solution": "Attach error handler to Sentry for automatic error reporting and diagnosis", "percentage": 85}]'::jsonb, 'Sapper, Express middleware', 'Errors caught and reported without raw error pages', 'Throwing errors without custom error boundary component', 0.86, 'haiku', NOW(), 'https://stackoverflow.com/questions/60341618/how-to-catch-a-server-error-in-sapper-svelte', 'admin:1764210798'),
('throw error vs return fail SvelteKit form actions difference', 'svelte', 'HIGH', '[{"solution": "Use throw error(status, message) for serious issues requiring +error.svelte display", "percentage": 94}, {"solution": "Use return fail(status, {details}) for validation errors to show in use:enhancement handler", "percentage": 96}]'::jsonb, 'SvelteKit +page.server.ts', 'Correct error/validation flow to error page or form handler', 'Confusing when to use error() vs fail() in form actions', 0.95, 'haiku', NOW(), 'https://stackoverflow.com/questions/76037679/what-are-the-differences-between-throw-error-and-return-fail-in-sveltekit', 'admin:1764210798'),
('Cannot find module App.svelte TypeScript type declarations', 'svelte', 'MEDIUM', '[{"solution": "Install and extend @tsconfig/svelte in tsconfig.json for Svelte type support", "percentage": 90}, {"solution": "Update /// <reference types=\"svelte\" /> instead of @sveltejs/kit in global.d.ts", "percentage": 88}]'::jsonb, 'TypeScript, Svelte with Rollup', 'TypeScript recognizes .svelte files without TS2307 errors', 'Missing @tsconfig/svelte dependency or stale global.d.ts reference', 0.89, 'haiku', NOW(), 'https://stackoverflow.com/questions/64194571/cannot-find-module-app-svelte-or-its-corresponding-type-declarations', 'admin:1764210798'),
('Cannot find module swiper SvelteKit SSR error dynamic import', 'svelte', 'MEDIUM', '[{"solution": "Dynamically import Swiper component in onMount: const module = await import(''./components/Slider.svelte'')", "percentage": 87}, {"solution": "Use <svelte:component this={SliderComponent}/> to render dynamically imported Swiper", "percentage": 89}]'::jsonb, 'SvelteKit, Swiper 7+', 'Swiper slider renders without SSR module resolution errors', 'Importing Swiper at module level in SSR context causes resolution failure', 0.88, 'haiku', NOW(), 'https://stackoverflow.com/questions/69113480/sveltekit-cannot-find-module-swiper', 'admin:1764210798');
