INSERT INTO knowledge_entries (query, category, hit_frequency, solutions, prerequisites, success_indicators, common_pitfalls, success_rate, claude_version, last_verified, source_url, contributor_email) VALUES
('Testing connection to computer failed: error due to lack of resources', 'testing', 'HIGH', '[{"solution": "Use -Quiet flag with Test-Connection to suppress errors: Test-Connection -ComputerName $computername -Quiet -Count 1 -ErrorAction SilentlyContinue", "percentage": 95}, {"solution": "Check connection object directly with .PingSucceeded property: if ((Test-Connection $computername).PingSucceeded)", "percentage": 90}]'::jsonb, 'PowerShell 5.1+, network access to target computers', 'Script continues without throwing errors, successfully identifies unreachable hosts in CSV output', 'Using assignment operator (=) instead of comparison (-ne or -eq), not handling null responses, missing -ErrorAction SilentlyContinue', 0.92, 'haiku', NOW(), 'https://stackoverflow.com/questions/46327414/using-test-connection-and-if-true-statement-else-not-working-when-no-connec', 'admin:1764190721'),
('PowerShell Test-Connection slow performance on large IP lists', 'testing', 'HIGH', '[{"solution": "Use runspaces to parallelize Test-Connection across multiple threads, achieving 254 IPs in ~10 seconds with $Threshold=100", "percentage": 94}, {"solution": "Use Start-Job -AsJob with Test-Connection and manage with Get-Job for asynchronous testing", "percentage": 88}]'::jsonb, 'PowerShell 5.1+, runspace factory API understanding, CSV import capability', 'Results exported to CSV show mixed Success/Failed statuses, all IPs processed within acceptable timeframe', 'Not adjusting $Threshold parameter for system resources, using Count/TimeoutSeconds defaults without optimization, missing $RunspacePool.Dispose() cleanup', 0.91, 'haiku', NOW(), 'https://stackoverflow.com/questions/70178502/powershell-test-connection-efficient-method', 'admin:1764190721');
