INSERT INTO knowledge_entries (query, category, hit_frequency, solutions, prerequisites, success_indicators, common_pitfalls, success_rate, claude_version, last_verified, source_url, contributor_email) VALUES
('How to skip null items in Vue v-for loop', 'vue', 'HIGH', '[{"solution": "Use v-if directive with null check: <li v-for=\"item in items\" v-if=\"item !== null\">. This filters null values directly in the template.", "percentage": 92}, {"solution": "Use computed property to filter null values before v-for: computed: { nonNullItems() { return this.items.filter(item => item !== null) } }", "percentage": 88}, {"solution": "In Vue 2, use filters before computed properties: Vue.filter(''removeNullProps'', obj => _.reject(obj, v => v === null))", "percentage": 75}]'::jsonb, 'Vue 2+ knowledge, understanding of v-if and v-for directives', 'Null items no longer render in the list, nested children properly filtered', 'Forgetting v-if when using v-for on null-containing arrays, using deprecated filters in Vue 2.5+, incorrect track-by attribute usage', 0.91, 'haiku', NOW(), 'https://stackoverflow.com/questions/36205625/skip-null-items-and-null-children-in-vue-v-for', 'admin:1764192152');
INSERT INTO knowledge_entries (query, category, hit_frequency, solutions, prerequisites, success_indicators, common_pitfalls, success_rate, claude_version, last_verified, source_url, contributor_email) VALUES
('Vue component receiving null instead of empty string in v-model binding', 'vue', 'HIGH', '[{"solution": "Replace v-model with explicit :value and @input bindings: :value=\"customer.address || ''''\" @input=\"customer.address = event.target.value\"", "percentage": 94}, {"solution": "Initialize data property with empty string instead of null: data() { return { customer: { address: '''' } } }", "percentage": 89}, {"solution": "Ensure child component properly accepts value prop and implements v-model correctly with proper prop binding", "percentage": 82}]'::jsonb, 'Vue component prop passing, v-model directive understanding, parent-child component communication', 'Null values display as empty string, v-model binding works bidirectionally without displaying null', 'Not initializing data properties with default values, mixing v-model with :value, incorrect prop naming (kebab-case vs camelCase)', 0.90, 'haiku', NOW(), 'https://stackoverflow.com/questions/49687110/replace-null-value-with-an-empty-string-vue', 'admin:1764192152');
INSERT INTO knowledge_entries (query, category, hit_frequency, solutions, prerequisites, success_indicators, common_pitfalls, success_rate, claude_version, last_verified, source_url, contributor_email) VALUES
('Vue v-if statement not detecting null or empty string values correctly', 'vue', 'HIGH', '[{"solution": "Use simple truthy check: <div v-if=\"archiveNote\"> instead of <div v-if=\"archiveNote === null || archiveNote === ''''\" to check all 8 falsy values (null, undefined, 0, '''', false, NaN, -0, 0n)", "percentage": 95}, {"solution": "Use double bang operator for explicit boolean conversion: <div v-if=\"!!archiveNote\"> which evaluates all falsy values to false", "percentage": 88}, {"solution": "Ensure props are using camelCase in component definition and kebab-case when passing from parent: :archive-note=\"archiveNote\" maps to prop archiveNote", "percentage": 85}]'::jsonb, 'Vue props system, JavaScript falsy values, kebab-case vs camelCase naming conventions', 'Conditional div displays only when variable is truthy, null/empty values properly hidden, parent-child prop binding correct', 'Using overly complex null checks instead of truthy evaluation, mismatched prop naming between parent and child components, forgetting that empty string is falsy', 0.92, 'haiku', NOW(), 'https://stackoverflow.com/questions/60779512/vue-v-if-statement-to-check-if-variable-is-empty-or-null', 'admin:1764192152');
