INSERT INTO knowledge_entries (query, category, hit_frequency, solutions, prerequisites, success_indicators, common_pitfalls, success_rate, claude_version, last_verified, source_url, contributor_email) VALUES
('LMDB Cache error - filename directory name or volume label syntax is incorrect', 'bundler', 'HIGH', '[{"solution": "Modify node_modules/@parcel/core/lib/resolveOptions.js:112 to use FSCache instead of LMDBCache - remove the LMDBCache branch", "percentage": 85}, {"solution": "Delete .parcel-cache folder and reinstall parcel", "percentage": 60}]'::jsonb, 'Parcel v2, Node.js installed', 'No LMDB errors, project builds successfully', 'Not checking Parcel version compatibility, only clearing cache without code fix', 0.78, 'haiku', NOW(), 'https://stackoverflow.com/questions/71208233', 'admin:1764227081'),
('Parcel build target main declares output file path of index.js which does not match compiled bundle type html', 'bundler', 'HIGH', '[{"solution": "Remove the main field from package.json completely", "percentage": 95}, {"solution": "Add targets.main: false to package.json instead of removing main field", "percentage": 90}]'::jsonb, 'Parcel v2, HTML entry point', 'Build completes without namer-default errors', 'Setting main to index.html when using HTML entry point', 0.92, 'haiku', NOW(), 'https://stackoverflow.com/questions/71053013', 'admin:1764227081'),
('Parcel command not found - global installation deprecation', 'bundler', 'MEDIUM', '[{"solution": "Use npx parcel instead of global installation: npx parcel index.html", "percentage": 88}, {"solution": "Use yarn: yarn add parcel-bundler and yarn parcel index.html", "percentage": 80}, {"solution": "Install locally and use npm scripts: npm run parcel", "percentage": 85}]'::jsonb, 'npm or yarn installed, Parcel package', 'Parcel commands execute without command not found errors', 'Relying on global installation instead of npx or local installation', 0.84, 'haiku', NOW(), 'https://stackoverflow.com/questions/65346296', 'admin:1764227081'),
('Key 216aea59fec25090 not found in cache - LMDBCache blob retrieval failed', 'bundler', 'HIGH', '[{"solution": "Delete the .parcel-cache folder: rm -rf .parcel-cache", "percentage": 92}, {"solution": "Ensure Python is installed and in PATH before building", "percentage": 78}, {"solution": "Update Parcel to latest version and clear cache", "percentage": 85}]'::jsonb, 'Parcel v2, Python executable in PATH', 'No cache key errors, build completes', 'Not checking Python installation, only deleting cache folder', 0.88, 'haiku', NOW(), 'https://stackoverflow.com/questions/74609097', 'admin:1764227081'),
('Parcel no entries found error - cannot resolve entry', 'bundler', 'MEDIUM', '[{"solution": "Add source field to package.json: {\"source\": \"index.html\"}", "percentage": 89}, {"solution": "Use npm run start instead of parcel start directly", "percentage": 75}]'::jsonb, 'Parcel v1 or v2, index.html present', 'parcel start or npm run start resolves entry without error', 'Not specifying source in package.json, wrong command format', 0.82, 'haiku', NOW(), 'https://stackoverflow.com/questions/59040545', 'admin:1764227081'),
('Parcel source map napi-rs upgrade breaks dev server - command hangs', 'bundler', 'MEDIUM', '[{"solution": "Uninstall parcel, delete .parcel-cache, reinstall: npm uninstall parcel && rm -rf .parcel-cache && npm install parcel", "percentage": 88}, {"solution": "Revert to @parcel/source-map v2.0.2 or earlier", "percentage": 82}]'::jsonb, 'Parcel v2.2.2+, Node.js 14+', 'npx parcel src/index.html starts dev server without hanging', 'Not clearing cache after reinstalling, using development version', 0.85, 'haiku', NOW(), 'https://stackoverflow.com/questions/70762597', 'admin:1764227081'),
('Cannot find module parcel-bundler/src/Logger - plugin version mismatch', 'bundler', 'LOW', '[{"solution": "Download missing files from parcel GitHub: Logger.js, prettyError.js, emoji.js to node_modules/@parcel/core/src/", "percentage": 72}, {"solution": "Use compatible parcel-plugin version for your parcel-bundler version", "percentage": 65}]'::jsonb, 'parcel-bundler installed, parcel plugins', 'Plugin loads without missing Logger module errors', 'Using outdated plugins with newer Parcel versions, not checking plugin compatibility', 0.68, 'haiku', NOW(), 'https://stackoverflow.com/questions/62870976', 'admin:1764227081'),
('Cannot find module sass - SCSS build fails without sass dependency', 'bundler', 'HIGH', '[{"solution": "Install sass package: npm install -D sass", "percentage": 94}, {"solution": "Avoid node-sass which has issues with newer Node versions", "percentage": 88}]'::jsonb, 'Parcel project with .scss files', 'SCSS files compile without sass resolution errors', 'Using node-sass instead of sass, not installing SCSS processor', 0.91, 'haiku', NOW(), 'https://stackoverflow.com/questions/58051539', 'admin:1764227081'),
('Parcel build fails - plugin is not a function in cssnano/postcss', 'bundler', 'MEDIUM', '[{"solution": "Downgrade cssnano to 4.1.11: npm install -D [email protected]", "percentage": 85}, {"solution": "Use --no-minify flag as workaround: parcel build index.html --no-minify", "percentage": 78}]'::jsonb, 'Parcel with CSS processing, cssnano plugin', 'CSS builds without plugin function errors', 'Using latest cssnano with parcel v1, not specifying compatible versions', 0.81, 'haiku', NOW(), 'https://stackoverflow.com/questions/67951491', 'admin:1764227081'),
('Parcel cannot find module axios after bundling - missing dependency resolution', 'bundler', 'MEDIUM', '[{"solution": "Add browserslist to package.json: {\"browserslist\": \"> 0.5%, last 2 versions, not dead\"}", "percentage": 82}, {"solution": "Set type module in package.json: {\"type\": \"module\"}", "percentage": 79}]'::jsonb, 'axios installed, Parcel v2, package.json', 'axios imports resolve without module not found errors', 'Not configuring browserslist, missing type field in package.json', 0.80, 'haiku', NOW(), 'https://stackoverflow.com/questions/70462514', 'admin:1764227081'),
('Module not found at runtime - Parcel asset path resolution in split bundles', 'bundler', 'MEDIUM', '[{"solution": "Import assets in parent/main component and pass as props instead of using absolute paths", "percentage": 75}, {"solution": "Use relative paths from component location for image imports", "percentage": 70}]'::jsonb, 'Parcel v2, code splitting enabled', 'Assets load without module not found errors in split bundles', 'Using absolute paths for assets in code-split components, importing in wrong component', 0.72, 'haiku', NOW(), 'https://stackoverflow.com/questions/54509389', 'admin:1764227081'),
('SyntaxError Unexpected token when using Jest and Parcel - JSX HTM compatibility', 'bundler', 'LOW', '[{"solution": "Configure Jest to use babel-jest for JSX transformation: add jest config with babel preset", "percentage": 68}, {"solution": "Use create-react-app instead of Parcel if Jest is required", "percentage": 55}]'::jsonb, 'Jest, Parcel, JSX/HTM project', 'Jest tests run without unexpected token syntax errors', 'Not configuring Jest Babel transform, mixing tool versions', 0.62, 'haiku', NOW(), 'https://stackoverflow.com/questions/62190644', 'admin:1764227081'),
('Uncaught SyntaxError invalid or unexpected token - UTF-8 encoding mismatch in bundle', 'bundler', 'MEDIUM', '[{"solution": "Reinstall Parcel: npm uninstall parcel && npm install parcel", "percentage": 83}, {"solution": "Clear .parcel-cache folder: rm -rf .parcel-cache", "percentage": 80}, {"solution": "Check system UTF-8 encoding matches source files", "percentage": 65}]'::jsonb, 'Parcel v2, UTF-8 encoded source files', 'Bundle runs without syntax errors, invalid characters in bundle output disappear', 'Not clearing build cache on installation, incorrect system encoding settings', 0.79, 'haiku', NOW(), 'https://stackoverflow.com/questions/68191788', 'admin:1764227081'),
('TypeScript SyntaxError Unexpected token - Babel transformer not configured', 'bundler', 'MEDIUM', '[{"solution": "Recreate project with create-react-app instead of manual Parcel setup", "percentage": 80}, {"solution": "Ensure @parcel/transformer-typescript is installed as dev dependency", "percentage": 75}]'::jsonb, 'TypeScript, React, Parcel setup', 'TypeScript compiles without unexpected token errors', 'Not using proper React/TypeScript starter, missing transformers', 0.77, 'haiku', NOW(), 'https://stackoverflow.com/questions/62648047', 'admin:1764227081'),
('Unexpected token name expected punc - Terser minification ES6+ method shorthand syntax', 'bundler', 'MEDIUM', '[{"solution": "Use --no-minify flag for build: parcel build index.html --no-minify", "percentage": 88}, {"solution": "Configure Terser to support ES6+ properly in .terserrc config", "percentage": 72}]'::jsonb, 'Parcel with Terser minifier, ES6+ code', 'Production build completes without minification syntax errors', 'Using outdated minifier configuration, not checking library ES6+ support', 0.80, 'haiku', NOW(), 'https://stackoverflow.com/questions/70695146', 'admin:1764227081'),
('Parcel transformer unexpected token with React 18 - JSX pragma configuration', 'bundler', 'LOW', '[{"solution": "Update .babelrc or babel config to support React 18 JSX transform", "percentage": 70}, {"solution": "Update Parcel to latest version with React 18 support", "percentage": 78}]'::jsonb, 'React 18, Parcel transformer-js', 'JSX code parses without unexpected token errors', 'Using outdated Babel configuration for React 18, old Parcel version', 0.74, 'haiku', NOW(), 'https://stackoverflow.com/questions/72419324', 'admin:1764227081'),
('TailwindCSS v4 unexpected token CloseSquareBracket - automatic source detection scanning node_modules', 'bundler', 'HIGH', '[{"solution": "Upgrade TailwindCSS to v4.0.18+: npm install -D [email protected]", "percentage": 92}, {"solution": "Create .gitignore with /node_modules/ to disable scanning", "percentage": 85}, {"solution": "Use @source directive to disable automatic detection: @import tailwindcss source(none)", "percentage": 82}]'::jsonb, 'TailwindCSS v4, Parcel bundler', 'CSS builds without CloseSquareBracket parsing errors', 'Using TailwindCSS v4.0.18 below, not excluding node_modules from source detection', 0.88, 'haiku', NOW(), 'https://stackoverflow.com/questions/79424674', 'admin:1764227081'),
('Parcel did not inject JS and CSS code to HTML - bundle-text CSS import conflict', 'bundler', 'MEDIUM', '[{"solution": "Use bundle-text for all CSS imports: import stylesString from \"bundle-text:./styles.scss\"", "percentage": 76}, {"solution": "Avoid mixing bundle-text and regular CSS imports in same project", "percentage": 72}]'::jsonb, 'Parcel with bundle-text transformer, CSS imports', 'Both bundle-text and regular CSS imports work or one approach consistently used', 'Mixing bundle-text and regular imports, expecting inline CSS from regular imports', 0.74, 'haiku', NOW(), 'https://stackoverflow.com/questions/71678060', 'admin:1764227081'),
('bundle-text CSS import and regular CSS import conflict - parcel transformer priority', 'bundler', 'LOW', '[{"solution": "Use sass-to-string package to transform SCSS to CSS string first", "percentage": 65}, {"solution": "Avoid using bundle-text:*.scss - use sass-to-string or bundle-text:*.css only", "percentage": 68}]'::jsonb, 'Parcel @parcel/transformer-inline-string, SCSS files', 'Either bundle-text or regular CSS imports work without conflicts', 'Mixing SCSS with bundle-text protocol, not using sass-to-string converter', 0.66, 'haiku', NOW(), 'https://stackoverflow.com/questions/71260911', 'admin:1764227081');
