INSERT INTO knowledge_entries (query, category, hit_frequency, solutions, prerequisites, success_indicators, common_pitfalls, success_rate, claude_version, last_verified, source_url, contributor_email) VALUES
('Remix Hydration Error: UI on server and client do not match', 'frontend', 'HIGH', '[{"solution": "Add suppressHydrationWarning prop to mismatched element: <p suppressHydrationWarning>", "percentage": 85}, {"solution": "Return current time from loader and use useLoaderData in useEffect to sync client and server time", "percentage": 80}, {"solution": "Disable browser extensions injecting styles (Grammarly, etc) before hydration", "percentage": 75}]'::jsonb, 'React 18, Remix SSR', 'Page hydrates without warnings, UI matches server render', 'Using Math.random() or new Date() without loader data, extensions injecting HTML', 0.85, 'haiku', NOW(), 'https://stackoverflow.com/questions/79198635/remix-hydration-error-when-rendering-current-time', 'admin:1764224696'),

('Cannot get returned errors from useFetcher form submission', 'frontend', 'HIGH', '[{"solution": "Use session.flash() to store action errors and read in loader", "percentage": 90}, {"solution": "Return error data from action and check fetcher.data for errors", "percentage": 85}]'::jsonb, 'Remix actions, useFetcher hook', 'Action errors accessible via fetcher.data', 'Trying to pass data directly from action to form without revalidation', 0.87, 'haiku', NOW(), 'https://stackoverflow.com/questions/75536646/using-remix-run-how-do-you-get-returned-errors-from-a-usefetcher-form-submissio', 'admin:1764224696'),

('Suppress Remix ErrorResponseImpl console errors for 404s', 'backend', 'MEDIUM', '[{"solution": "Add ErrorBoundary to root.tsx to catch and suppress errors", "percentage": 88}, {"solution": "Set NODE_ENV=production to suppress No route matches URL logs", "percentage": 80}, {"solution": "Create catch-all route $.tsx with 404 response to intercept unmatched URLs", "percentage": 85}]'::jsonb, 'Express/Node.js Remix server', 'No ErrorResponseImpl logs in console', 'Not adding error boundary, improper NODE_ENV configuration', 0.84, 'haiku', NOW(), 'https://stackoverflow.com/questions/78076280/suppress-remix-errorresponseimpl-errors', 'admin:1764224696'),

('Buffer is not defined error - MongoDB in loader function', 'backend', 'MEDIUM', '[{"solution": "Rename database file to db.server.ts to exclude from client bundle", "percentage": 95}, {"solution": "Only import MongoDB functions from .server.ts files, never from client code", "percentage": 92}]'::jsonb, 'Remix server bundling, MongoDB', 'Loader executes on server without client-side errors', 'Importing node modules (MongoDB) in shared code, not using .server.ts suffix', 0.93, 'haiku', NOW(), 'https://stackoverflow.com/questions/77807498/getting-an-error-thrown-from-my-loader-function-in-remixjs-on-the-browser', 'admin:1764224696'),

('React hydration error with Material-UI Emotion cache', 'frontend', 'MEDIUM', '[{"solution": "Create new Emotion cache on every server request in entry.server.tsx", "percentage": 88}, {"solution": "Clear browser extensions that inject HTML/CSS before hydration", "percentage": 82}]'::jsonb, 'Material-UI, Emotion SSR', 'Hydration completes without CSS mismatch', 'Reusing Emotion cache across requests, browser extensions modifying DOM', 0.85, 'haiku', NOW(), 'https://stackoverflow.com/questions/74297473/remix-is-returning-hydration-error-when-attempting-to-map-data', 'admin:1764224696'),

('ErrorBoundary does not show when session secret is missing', 'backend', 'MEDIUM', '[{"solution": "Initialize all environment variables before loading routes", "percentage": 80}, {"solution": "Add ErrorBoundary to root.tsx to catch synchronous initialization errors", "percentage": 85}]'::jsonb, 'Environment variables, Remix entry.server', 'ErrorBoundary renders instead of blank page', 'Checking env vars inside loader instead of root level, missing ErrorBoundary', 0.82, 'haiku', NOW(), 'https://stackoverflow.com/questions/75512908/remix-run-server-error-error-boundary-doesnt-show', 'admin:1764224696'),

('Error does not bubble up to root ErrorBoundary', 'frontend', 'HIGH', '[{"solution": "Use throw json() or throw new Response() for response errors - catches CatchBoundary", "percentage": 90}, {"solution": "Use throw new Error() for regular errors - catches ErrorBoundary", "percentage": 88}, {"solution": "Add ErrorBoundary in the specific route where error occurs", "percentage": 78}]'::jsonb, 'Remix error handling', 'Errors caught and displayed by boundary component', 'Confusing throw Error vs throw Response, not understanding which boundary catches which', 0.88, 'haiku', NOW(), 'https://stackoverflow.com/questions/73135431/remix-if-a-route-doesnt-have-an-error-boundary-the-error-doesnt-bubble-up', 'admin:1764224696'),

('Turborepo: Unknown file extension ".ts" in Remix example', 'backend', 'LOW', '[{"solution": "Add serverDependenciesToBundle config: [\"package-name\"]", "percentage": 92}, {"solution": "Watch additional paths in remix.config.ts: watchPaths: [\"../../packages/name\"]", "percentage": 88}]'::jsonb, 'Turborepo, monorepo, Remix', 'Remix example runs without module resolution errors', 'Forgetting serverDependenciesToBundle for workspace packages', 0.90, 'haiku', NOW(), 'https://stackoverflow.com/questions/77253606/remix-throwing-strange-errors-when-used-in-turborepo', 'admin:1764224696'),

('Loader function returns null to useLoaderData', 'frontend', 'HIGH', '[{"solution": "Loaders must be exported from route modules only - not nested components", "percentage": 95}, {"solution": "Wrap returned data with json() helper: return json(data)", "percentage": 85}, {"solution": "Verify loader is exported as named export from route file", "percentage": 92}]'::jsonb, 'Remix routing, loaders', 'useLoaderData returns data from loader', 'Defining loader in non-route file, forgetting json() wrapper, loader not exported', 0.91, 'haiku', NOW(), 'https://stackoverflow.com/questions/76474735/loader-function-not-returning-the-data-in-remix-run', 'admin:1764224696'),

('Cannot programmatically trigger loader reload', 'frontend', 'MEDIUM', '[{"solution": "Use useFetcher hook with .load() method: fetcher.load(\"/path\")", "percentage": 88}, {"solution": "Use useNavigate to navigate to current route: navigate(\".\", {replace: true})", "percentage": 85}, {"solution": "Submit empty form to action to trigger loader revalidation", "percentage": 75}]'::jsonb, 'Remix navigation, loaders', 'Loader executes on demand via UI action', 'Using useSubmit for data-only refresh, not understanding useFetcher.load()', 0.83, 'haiku', NOW(), 'https://stackoverflow.com/questions/72670869/remix-programatically-trigger-loader', 'admin:1764224696'),

('Shared object between loader/action not updating after action called', 'backend', 'MEDIUM', '[{"solution": "Recreate entire object after update: db = {...db, key: ''new''}''", "percentage": 85}, {"solution": "Use proper data flow: loader fetches data, action updates DB, loader returns fresh data", "percentage": 92}]'::jsonb, 'JavaScript object reference, Remix data flow', 'Action updates reflected in next loader call', 'Mutating object in-place, not understanding immutability in Remix', 0.88, 'haiku', NOW(), 'https://stackoverflow.com/questions/71897869/shared-object-between-loader-action-does-not-update-after-action-is-called-in', 'admin:1764224696'),

('Why does returning plain object from loader work instead of json()?', 'frontend', 'MEDIUM', '[{"solution": "Remix auto-converts plain objects to JSON responses automatically", "percentage": 90}, {"solution": "Use json() helper for custom headers/status codes, not required for basic data", "percentage": 85}, {"solution": "With Single Fetch, return plain objects for better turbo-stream support", "percentage": 82}]'::jsonb, 'Remix loaders, JSON serialization', 'useLoaderData receives data regardless of json() wrapper', 'Assuming json() is always required, confusion about Remix serialization', 0.87, 'haiku', NOW(), 'https://stackoverflow.com/questions/78837911/why-does-returning-plain-object-from-a-loader-in-remix-work', 'admin:1764224696'),

('Cannot access loader data inside action function', 'backend', 'HIGH', '[{"solution": "Pass loader data via FormData, URLSearchParams, or URL params to action", "percentage": 88}, {"solution": "Store data in session or cookies, read in both loader and action", "percentage": 85}]'::jsonb, 'Remix actions, loaders', 'Action receives loader data via form submission', 'Expecting hooks (useLoaderData) to work in server functions, not understanding data flow', 0.89, 'haiku', NOW(), 'https://stackoverflow.com/questions/72441508/can-i-access-loader-data-in-action-function-in-remix-run', 'admin:1764224696'),

