INSERT INTO knowledge_entries (query, category, type, solutions, executable_type, prerequisites, common_pitfalls, success_indicators, preview_summary, source_url, contributor_email)
VALUES (
  'Web Application Testing - Toolkit for interacting with and testing local web applications using Playwright',
  'claude-code',
  'skill',
  '[
    {"solution": "Start single server and run automation", "cli": {"macos": "python scripts/with_server.py --server \"npm run dev\" --port 5173 -- python automation.py", "linux": "python scripts/with_server.py --server \"npm run dev\" --port 5173 -- python automation.py", "windows": "python scripts/with_server.py --server \"npm run dev\" --port 5173 -- python automation.py"}, "manual": "Use with_server.py helper script to manage server lifecycle. Run with --help first to see usage options. Specify server command with --server and port with --port, then pass automation script after -- separator. Script automatically waits for server to be ready before running your automation.", "note": "Always use the with_server.py helper for managing server lifecycle. Do not manually start servers."},
    {"solution": "Start multiple servers (backend + frontend)", "cli": {"macos": "python scripts/with_server.py --server \"cd backend && python server.py\" --port 3000 --server \"cd frontend && npm run dev\" --port 5173 -- python automation.py", "linux": "python scripts/with_server.py --server \"cd backend && python server.py\" --port 3000 --server \"cd frontend && npm run dev\" --port 5173 -- python automation.py", "windows": "python scripts/with_server.py --server \"cd backend && python server.py\" --port 3000 --server \"cd frontend && npm run dev\" --port 5173 -- python automation.py"}, "manual": "For applications with multiple services, repeat --server and --port pairs for each service. The script starts all servers in sequence, waits for each to be ready on its port, then runs your automation. Number of --server and --port arguments must match.", "note": "All servers must be ready before automation runs. Script enforces 30-second timeout per server by default."},
    {"solution": "Discover page elements before automation", "cli": {"macos": "python examples/element_discovery.py", "linux": "python examples/element_discovery.py", "windows": "python examples/element_discovery.py"}, "manual": "Use element_discovery.py example to find all buttons, links, and input fields on a page. Navigates to the page, waits for networkidle, discovers interactive elements, and takes a screenshot. This is the reconnaissance step before writing specific selectors.", "note": "Always call wait_for_load_state(''networkidle'') before inspecting the DOM on dynamic apps."},
    {"solution": "Automate static HTML files", "cli": {"macos": "python examples/static_html_automation.py", "linux": "python examples/static_html_automation.py", "windows": "python examples/static_html_automation.py"}, "manual": "For static HTML files, use file:// URLs. Use os.path.abspath() to get full path, then construct file:// URL. Navigate with page.goto(file_url), interact with elements using click/fill, and capture screenshots. No server needed for static content.", "note": "Static HTML does not require with_server.py helper."},
    {"solution": "Capture and debug console logs", "cli": {"macos": "python examples/console_logging.py", "linux": "python examples/console_logging.py", "windows": "python examples/console_logging.py"}, "manual": "Attach console message handler with page.on(''console'', handler) before navigation. Console logs are captured as objects with type and text properties. Save logs to file for debugging. Useful for frontend errors and debugging output.", "note": "Must attach handler before navigating to page. Logs are only captured after handler is registered."}
  ]'::jsonb,
  'script',
  'Python 3, Playwright library, npm (for example servers)',
  'Not waiting for networkidle on dynamic apps before inspecting DOM. Not using with_server.py helper for server lifecycle. Mixing server startup with automation logic instead of separation of concerns. Not matching number of --server and --port arguments.',
  'Server starts and accepts connections on specified port. Automation script runs without errors. Page elements are discovered and displayed. Screenshots are captured successfully. Console logs (if applicable) are captured and saved. Script exits cleanly without hanging processes.',
  'Toolkit for testing local web applications using Playwright with server lifecycle management, element discovery, and debugging capabilities.',
  'https://skillsmp.com/skills/anthropics-skills-webapp-testing-skill-md',
  'admin:HAIKU_SKILL_1764287718'
);
