INSERT INTO knowledge_entries (query, category, type, solutions, executable_type, prerequisites, common_pitfalls, success_indicators, preview_summary, source_url, contributor_email)
VALUES (
  'Skill Development - Create modular skills for Claude Code plugins with progressive disclosure',
  'claude-code',
  'skill',
  '[{"solution": "Create skill structure with SKILL.md and optional resources", "cli": {"macos": "mkdir -p plugin-name/skills/skill-name/{references,examples,scripts} && touch plugin-name/skills/skill-name/SKILL.md", "linux": "mkdir -p plugin-name/skills/skill-name/{references,examples,scripts} && touch plugin-name/skills/skill-name/SKILL.md", "windows": "mkdir plugin-name\\skills\\skill-name\\{references,examples,scripts} and create SKILL.md"}, "manual": "1. Create directory: plugin-name/skills/skill-name/\n2. Create SKILL.md with frontmatter (name, description, version)\n3. Add markdown body with core concepts\n4. Create subdirectories: references/, examples/, scripts/\n5. Add supporting files as needed\n6. Reference bundled resources in SKILL.md body", "note": "Progressive disclosure: SKILL.md always loaded (1500-2000 words), references loaded as needed, scripts/examples used directly."}, {"solution": "Write effective SKILL.md with strong trigger description", "cli": {"macos": "cat > SKILL.md << ''EOF''\n---\nname: skill-name\ndescription: This skill should be used when user asks to \"trigger phrase 1\", \"trigger phrase 2\", \"trigger phrase 3\". Be specific and concrete.\nversion: 0.1.0\n---\n\n# Skill Name\n\nOverview and core concepts.\nEOF", "linux": "cat > SKILL.md << ''EOF''\n---\nname: skill-name\ndescription: This skill should be used when user asks to \"trigger phrase 1\", \"trigger phrase 2\", \"trigger phrase 3\". Be specific and concrete.\nversion: 0.1.0\n---\n\n# Skill Name\n\nOverview and core concepts.\nEOF", "windows": "Create SKILL.md with frontmatter: name, description with specific user phrases, version, and markdown body"}, "manual": "1. Use third-person in description: ''This skill should be used when...''\n2. Include specific trigger phrases users would say (not vague conditions)\n3. Write body in imperative/infinitive form (not second person)\n4. Keep SKILL.md lean: 1500-2000 words ideal, max 5000\n5. Move detailed content to references/ files\n6. Reference supporting files clearly in body", "note": "Good triggers: ''create a hook'', ''add PreToolUse event''. Bad: ''when you need hooks'', ''use for hooks''."}, {"solution": "Organize content with progressive disclosure", "cli": {"macos": "mkdir -p references/ examples/ scripts/ && echo ''## Additional Resources\\n\\nFor detailed patterns, see references/patterns.md\\nFor examples, see examples/'' >> SKILL.md", "linux": "mkdir -p references/ examples/ scripts/ && echo ''## Additional Resources\\n\\nFor detailed patterns, see references/patterns.md\\nFor examples, see examples/'' >> SKILL.md", "windows": "Create references/, examples/, scripts/ directories and reference them in SKILL.md"}, "manual": "1. Put core concepts and workflows in SKILL.md\n2. Move detailed patterns/advanced content to references/\n3. Store working code in examples/\n4. Create utility scripts in scripts/\n5. Reference all files from SKILL.md so Claude knows they exist\n6. Each reference file can be 2000-5000+ words", "note": "Progressive disclosure keeps context efficient: metadata always loaded, body on trigger, resources as needed."}]'::jsonb,
  'steps',
  'Understanding plugin structure, clarity on skill use cases, knowledge of markdown YAML frontmatter',
  'Weak trigger phrases (vague, not third-person), too much content in SKILL.md (>5000 words), second-person writing style (''you should''), forgetting to reference supporting files, duplication across SKILL.md and references/',
  'Skill appears in Claude when trigger phrases match, SKILL.md body uses imperative form (verb-first), references/ has detailed content, resources clearly documented and referenced from SKILL.md',
  'Create modular skills for Claude Code plugins using progressive disclosure for efficient context loading',
  'https://skillsmp.com/skills/anthropics-claude-code-plugins-plugin-dev-skills-skill-development-skill-md',
  'admin:HAIKU_SKILL_1764289548_81336'
);
